function [A,b] = discretizar(x, y, h)  nColumnas = length([x(1):h:x(2)]);  nFilas = length([y(1):h:y(2)]);  aux = zeros(nFilas, nColumnas);  for i=(1:nFilas)    for j = (1:nColumnas)      aux(i,j) = f((j-1)*h,(i-1)*h);    endfor  endfor  A = zeros((nFilas-2)*(nColumnas-2), (nFilas-2)*(nColumnas-2));  b = zeros((nFilas-2)*(nColumnas-2), 1);  cont = 1;  for i=(2:nFilas-1)    for j=(2:nColumnas-1)      for k=(1:nFilas)        for l=(1:nColumnas)          if k == i && l == j            A(cont, fAux(k,l, nColumnas)) = 4;          elseif k == i && (l == j+1 || l == j-1)            if !isnan(aux(k,l))              b(cont) = b(cont) + aux(k,l);            else              A(cont, fAux(k,l, nColumnas)) = 1;            endif          elseif l == j && (k == i+1 || k == i-1)            if !isnan(aux(k,l))              b(cont) = b(cont) + aux(k,l);            else              A(cont, fAux(k,l,nColumnas)) = 1;            endif          endif        endfor      endfor      cont = cont+1;    endfor  endfor
endfunction
function indice = fAux(i,j, nColumnas)  indice = (j-1)+((i-2)*(nColumnas-2));
endfunction
